<?php/** * The default template for displaying content * * Used for both single artist. * * @package WordPress * @subpackage Turbulence * @since Turbulence 1.0 */?><section class="artist-details">    <div class="artist-details__info">        <h1 class="artist-details__name"><?php the_title(); ?></h1>        <div class="artist-details__photo">            <?php echo get_artist_pic(get_the_ID()); ?>        </div>        <?php if($map = get_field('artist_map')):?>        <div class="acf-map">            <div class="marker" data-lat="<?php echo $map['lat']; ?>" data-lng="<?php echo $map['lng']; ?>"></div>        </div>        <?php else:?>        <div class="artist-details__map">        </div>        <?php endif;?>        <div class="artist-details__place"><?php echo get_field('location')?></div>    </div>    <div class="artist-details__content">        <div class="artist-details__desc">            <?php the_content(); ?>        </div>        <?php if($website = get_field('web_site')):?>        <a href="<?php echo $website?>" target="_blank" class="artist-details__site"><?php echo $website?></a>        <?php endif;?>        <div class="artist-details__social">            <?php if($value = get_field('facebook_link')):?>            <a href="<?php echo $value?>" class="social-fb"></a>            <?php endif;?>            <?php if($value = get_field('twitter_link')):?>            <a href="http://twitter.com/<?php echo $value?>" class="social-tw"></a>            <?php endif;?>            <?php if($value = get_field('github_link')):?>            <a href="http://github.com/<?php echo $value?>" class="social-git"></a>            <?php endif;?>        </div>        <?php        $commissions = get_posts(array(            'post_type'		=> 'commission',            'post__in'      => getCommissionsPostIds(array(get_post())),        ));        if(count($commissions)):?>            <!--    artist-details__works        -->            <div class="artist-details__relevant">                <ul class="artist-details__relevant-list">                    <?php foreach($commissions as $pItem):                        $arts = get_field('artist', $pItem->ID);                        $arts = is_array($arts) ? $arts : array($arts);                        $author = array();                        foreach($arts as $art) {                            $author[] = $art->post_title;                        }                        $author = implode(' && ', $author);                        $tags = array();                        foreach ( wp_get_post_tags($pItem->ID) as $tag ) {                            $tags[] = "<a href='".get_tag_link( $tag->term_id )."'>{$tag->name}</a>";                        }                        $color = get_field('color', $pItem->ID);                        $color = $color && $color != 'null' ? $color : false;                        list($thumb) = wp_get_attachment_image_src(get_post_thumbnail_id( $pItem->ID ), array(50, 50));                        list($preview) = wp_get_attachment_image_src(get_post_thumbnail_id( $pItem->ID ), 'custom-commision-size');                    ?>                        <li>                            <div                                class="i-item"                                data-href="<?php echo get_permalink($pItem->ID)?>">                                <a title="<?php echo $pItem->post_title?>" href="<?php echo get_permalink($pItem->ID)?>">                                    <img width="50" height="50" class="wp-post-image" src="<?php echo $thumb?>">                                </a>                                <div style="<?php echo ($color ? "box-shadow: 0px 0px 0px 4px {$color}; color: {$color};" : '')?> display: none;"                                class="i-item__popup <?php echo ($color ? 'i-item__popup_custom-color' : '')?>">                                <div class="i-item__popup-frame frame-type-<?php echo (get_field('list_frame_type', $pItem->ID) ? get_field('list_frame_type', $pItem->ID) : 'default')?>"></div>                                <a class="i-item__popup-img"                                   href="<?php echo get_permalink($pItem->ID)?>"><img                                    class="i-item__popup-work" alt=""                                    src="<?php echo $preview?>"></a>                                <div class="i-item__popup-title"><?php echo $pItem->post_title?><span><?php echo $author?></span></div>                                <div class="i-item__popup-tags"><?php echo implode('', $tags)?></div>                            </div>                        </li>                    <?php endforeach;?>                </ul>            </div>            <?php endif;?>    </div></section>